Bash(powershell -File - <<'PSEOF'
Get-ChildItem -Recurse -Include '*.tsx' -Path 'app','components' | ForEach-Object {
    $content = Get-Content $_.FullName -Raw
    if \($content -match 'border-border'\) {
        $newContent = $content -replace 'border-border', 'border-teal/20'
        Set-Content $_.FullName -Value $newContent -NoNewline
        Write-Output \("Updated: " + $_.FullName\)
    }
}
PSEOF)
