# ============================================================
# MAIN SEED FUNCTION
# ============================================================

def seed_skills(conn=None):
    """Seed skill_lessons and pro_analysis_entries tables.
    Guard: skip if tables already have data (>20 lessons).
    """
    close_conn = False
    if conn is None:
        # Import get_db from main module (called at startup after init_db)
        from main import get_db
        conn = get_db()
        close_conn = True

    try:
        count = conn.execute("SELECT COUNT(*) FROM skill_lessons").fetchone()[0]
        if count > 20:
            logger.info("skill_lessons already seeded (%d entries), skipping", count)
            if close_conn:
                conn.close()
            return

        # ── Insert skill lessons ──
        all_lessons = (
            _shooting_lessons()
            + _shooting_advanced_lessons()
            + _skating_lessons()
            + _skating_advanced_lessons()
            + _puck_handling_lessons()
            + _puck_handling_advanced_lessons()
            + _awareness_lessons()
            + _awareness_advanced_lessons()
            + _positional_lessons()
            + _positional_advanced_lessons()
            + _goalie_lessons()
            + _team_play_lessons()
            + _checking_lessons()
            + _transition_lessons()
            + _leadership_lessons()
            + _game_situation_lessons()
            + _faceoff_lessons()
            + _communication_lessons()
            + _defensive_system_lessons()
            + _offensive_creativity_lessons()
            + _off_ice_lessons()
            + _drill_addon_lessons()
        )

        for lesson in all_lessons:
            conn.execute(
                """INSERT INTO skill_lessons
                   (id, title, series, lesson_number, category, description,
                    coaching_points, common_errors, skill_tags, positions, age_level)
                   VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)""",
                (
                    _uid(),
                    lesson["title"],
                    lesson.get("series"),
                    lesson.get("lesson_number", 1),
                    lesson["category"],
                    lesson.get("description", ""),
                    _j(lesson.get("coaching_points", [])),
                    _j(lesson.get("common_errors", [])),
                    _j(lesson.get("skill_tags", [])),
                    _j(lesson.get("positions", [])),
                    lesson.get("age_level", "all"),
                ),
            )

        logger.info("Seeded %d skill lessons", len(all_lessons))

        # ── Insert pro analysis entries ──
        pro_entries = _pro_analysis_entries()

        for entry in pro_entries:
            conn.execute(
                """INSERT INTO pro_analysis_entries
                   (id, concept_title, player_reference, description,
                    key_coaching_cues, what_to_look_for, skill_tags, positions, level)
                   VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)""",
                (
                    _uid(),
                    entry["concept_title"],
                    entry.get("player_reference"),
                    entry.get("description", ""),
                    _j(entry.get("key_coaching_cues", [])),
                    _j(entry.get("what_to_look_for", [])),
                    _j(entry.get("skill_tags", [])),
                    _j(entry.get("positions", [])),
                    entry.get("level", "elite"),
                ),
            )

        logger.info("Seeded %d pro analysis entries", len(pro_entries))
        conn.commit()

    except Exception as e:
        logger.error("Error seeding skills: %s", e)
        raise
    finally:
        if close_conn:
            conn.close()
